INSERT INTO ARTICLES (ID, TITLE, CONTENT, POSITION) VALUES(1, '关于我们', '<div color="468499"><br><br><br><h1><big><big><font color=#FFFFFF>关于我们</font></big></big></h1></div><imgtxt src="1_1.jpg" height="250" width="250"><h3>President</h3>谢珊珊 Shanshan Xie<br>sxie@wpi.edu<br>ECE ''18 </imgtxt><imgtxt src="1_2.jpg" height="250" width="250"><h3>Vice President</h3>刘嘉成 Jiacheng Liu<br>jliu4@wpi.edu<br>ME & CE ''18 </imgtxt><imgtxt src="1_3.jpg" height="250" width="250"><h3>Treasurer</h3>李思卓 Sizhuo Li<br>sli12@wpi.edu<br>ECE ''20</imgtxt><imgtxt src="1_4.jpg" height="250" width="250"><h3>Secretary</h3>金逸飞 Yifei Jin<br>yjin3@wpi.edu<br>ECE ''20</imgtxt><imgtxt src="1_5.jpg" height="250" width="250"><h3>Public Relations</h3>龙晓仪 Xiaoyi Long<br>xlong2@wpi.edu<br>ECE ''20</imgtxt><imgtxt src="1_6.jpg" height="250" width="250"><h3>Member Liaison</h3>腾馨宇 Xinyu Teng<br>cteng@wpi.edu<br>ECE ''20</imgtxt><imgtxt src="1_7.jpg" height="250" width="250"><h3>Webmaster</h3>宋沅达 Yuanda Song<br>ysong5@wpi.edu<br>CS ''20</imgtxt><imgtxt src="1_8.jpg" height="250" width="250"><h3>Chinese Corner Coordinator</h3>郝美 Mei Hao<br>mhao@wpi.edu<br>ME ''20</imgtxt>', 0);
INSERT INTO ARTICLES (ID, TITLE, CONTENT, POSITION) VALUES(2, '写在前面的话', '<div color="468499"><br><br><br><h1><big><big><font color=#FFFFFF>写在前面的话</font></big></big></h1></div>&emsp;&emsp;Hello 亲爱的学弟学妹们！ <br>&emsp;&emsp;再次感谢国家。顺便感谢党。再感谢一下cctv。 <br>&emsp;&emsp;Alright，我反正写不出感天动地的前言。那就，欢迎大家来到WPI 吧！ <br>&emsp;&emsp;我在这里快第四年了，感觉大学时光真的很美好，还记得上大学前的时候期待着开学的心情，刚来到学校英语磕磕碰碰但是却特别兴奋和期待，也记得被作业压的喘不过气但是做完后还是感觉这个世界很美好的。这里有很多热心帮助我们的学长学姐。就是因为他们，这三年，我觉得生活特别温暖和美好。 你们的大学时光才刚刚开始，希望大家都有一个美好的四年大学时光，收获让自己又爱又恨的好朋友 and 学业爱情双丰收。 <br>&emsp;&emsp;欢迎大家多参与我们CSA的活动！有什么问题都可以在微信群上问我们，或者发邮件给csa@wpi.edu！会有很多热心的学长！和学姐帮你们解 答各种人生路上的迷茫和 …… 迷茫 。么么哒！ <br>&emsp;&emsp;掌声鼓励日夜为sg 更新换代而奋斗的同学们！期待学弟学妹们的到来！ <br><br><p align="right">Cyan 谢珊珊 2018 ECE </p>', 1);
INSERT INTO ARTICLES (ID, TITLE, CONTENT, POSITION) VALUES(3, '编者按', '<div color="468499"><br><br><br><h1><big><big><font color=#FFFFFF>编者按</font></big></big></h1></div>&emsp;&emsp;Survive Guide (SG) 是汲取了WPI各级先辈们对在校生活和学习 的经验而编辑成的一本指南，目的是为了帮助新生们更好的了解在 WPI的生活和学习。去年暑假来WPI前，SG里面的内容也让我受益 匪浅。即使开学后，我也会经常去翻看SG的内容。可以说SG是WPI 的中文版Wikipedia。<br>&emsp;&emsp;第十一版丰富了内容，删除了前版的错误和已经过时不适用的内 容，并且更新了Publisher文件，方便以后的同学来完善SG。<br>&emsp;&emsp;感谢为了SG付出过的每一位同学！<br>&emsp;&emsp;感谢谢珊珊学姐和宁方鸣学长为我们带来了手机版的SG!<br>&emsp;&emsp;如果你对学校生活学习方面有什么疑问欢迎email至 csa@wpi.edu。<br>&emsp;&emsp;祝每一位即将来到WPI的小伙伴们有一个美好的大学生活！<br><br><p align="right">宋沅达 2020 CS</p>', 2);
INSERT INTO ARTICLES (ID, TITLE, CONTENT, POSITION) VALUES(4, '常用链接', '<div color="468499"><br><br><br><h1><big><big><font color=#FFFFFF>常用链接</font></big></big></h1></div><tab><b>学校主页</b><br>http://www.wpi.edu<tbr><b>WPI校园地图</b><br>http://www.wpi.edu/about/visitors/campusmap.html<tbr><b>学年日历</b><br>http://www.wpi.edu/calendars/index.html<tbr><b>Canvas</b><br>https://canvas.wpi.edu/<br>WPI的课程系统。教授会在里面发相关课程的大纲、作业、试验、阅读材料等等。<tbr><b>Banner System</b><br>http://bannerweb.wpi.edu<br>可以选课、查看成绩、注册等等。<tbr><b>邮件系统</b><br>http://exchange.wpi.edu<br>上面可以查到一学年中各门课的时间，地点和老师。<tbr><b>Catalog</b><br>http://www.wpi.edu/Pubs/Catalogs/Ugrad/index.html<tbr><b>Helpdesk</b><br>http://www.wpi.edu/Academics/CCC/Help/<br>可以帮助解决大多数的电脑问题<tbr><b>查询教授评价</b><br>www.ratemyprofessors.com<br>Or<br>http://bannerweb.wpi.edu<br>在banner登录之后, Student Services & Financial Aid > Additional Information for Students > WPI Student Course Reports</tab>', 3);
INSERT INTO ARTICLES (ID, TITLE, CONTENT, POSITION) VALUES(5, '常见缩写', '<div color="468499"><br><br><br><h1><big><big><font color=#FFFFFF>常见缩写</font></big></big></h1></div><font size="5">校园常见缩写</font><br><br><b>WPI</b>&emsp;Worcester Polytechnic Institute<br><b>CSA</b>&emsp;Chinese Student Association<br><b>CDC</b>&emsp;Career Development Center<br><b>CHE</b>&emsp;Chemical Engineering<br><b>CS</b>&emsp;Computer Science<br><b>ECE</b>&emsp;Electrical and Computer Engineering<br><b>IQP</b>&emsp;Interactive Qualifying Project<br><b>MQP</b>&emsp;Major Qualifying Project<br><b>HUA</b>&emsp;Humanity and Art<br><b>MBTA</b>&emsp;Mass Bay Transmit Authority<br><b>NSO</b>&emsp;New Student Orientation<br><b>TA</b>&emsp;Teaching Assistant<br><b>SA</b>&emsp;Senior Assistant<br><b>PLA</b>&emsp;Peer Learning Assistant<br><b>SSN</b>&emsp;Social Security Number<br><br><font size="5">聊天常见缩写</font><br><br><b>PPL</b>&emsp;People<br><b>ASAP</b>&emsp;as soon as possible<br><b>RSVP</b>&emsp;就是让你赶紧注册的意思<br><b>LMAO</b>&emsp;Laughing my ass off<br><b>MEME</b>&emsp;不是我们国内说的表情包，而是以图片为载体的"梗"，可以google一下<br><b>IDK</b>&emsp;I don''t know<br>', 4);
