INSERT INTO ARTICLES (TITLE, CONTENT ,MENU_ID) VALUES('<span style="font-weight:bold;font-size:50px;color:grey;">关于我们</span>', '<img src="1_1.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">President</span><br>陆安琪 Anqi Lu<br>alu@wpi.edu<br>Computer Science & Mathematical Science ''18</img><img src="1_2.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">Vice President</span><br>周梓雨 Ziyu Zhou<br>zzhou2@wpi.edu<br>Management Information System ''17</img><img src="1_3.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">Treasurer</span><br>刘嘉成 Jason Liu<br>jliu4@wpi.edu<br>Mechanical Engineering ''18</img><img src="1_4.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">Secretary</span><br>孙曜 Sun Yao<br>ysun8@wpi.edu<br>Mechanical Engineering ''18</img><img src="1_5.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">Public Relations</span><br>谢珊珊 Cyan Xie<br>sxie@wpi.edu<br>Electrical and Computer Engineering ''18</img><img src="1_6.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">Member Liaison</span><br>戴荻狄 Didi Dai<br>ddai@wpi.edu<br>Industrial Engineering & Management Information System ''18</img><img src="1_7.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">Webmaster</span><br>王耀奉 Yaofeng Wang<br>ywang15@wpi.edu<br>Computer Science & Industrial Engineering ''19</img><img src="1_8.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">Chinese Corner Coordinator</span><br>张晋玮 Jinwei Zhang<br>jzhang8@wpi.edu<br>Robotics Engineering & Electrical andComputer Engineering ''18</img><img src="1_9.jpg" height="250" width="250"><span style="font-size:14px;font-weight:bold;">Sports Coordinator</span><br>孙东圣 Dongsheng Sun<br>dsun2@wpi.edu<br>Computer Science & IMGD ''18</img>', 1);
INSERT INTO ARTICLES (TITLE, CONTENT ,MENU_ID) VALUES('<span style="font-weight:bold;font-size:50px;color:grey;">写在前面的话</span>', '&emsp;&emsp;欢迎WPI 2020届的同学！<br>&emsp;&emsp;她包容的环境，当然最重要的是这里可爱的人。不仅是老师同学，校园里几乎每个Staff都会对你亲切的微笑。毕业典礼之后，54岁清洁工拿到 WPI Mechanical Engineering Degree的新闻在社交网络中被广泛留传。虽然这件事情发生的几率并不大，但它体现出了WPI一种奋发向上的精神。我希望你们，也能在接下来的一年里有所收获。<br>&emsp;&emsp;这本SG是许多学长学姐用心写出来的。这么多年大家在那边的一些心得和经验，希望能多多少少帮到你们一点。特别感谢一直在操心这本 SG 我们CSA的 Webmaster 王耀奉同学，花费了好多个日日夜夜整理材料，辛苦啦。<p align="right">陆安琪<br>2016年6月1日</p>', 2);
INSERT INTO ARTICLES (TITLE, CONTENT ,MENU_ID) VALUES('<span style="font-weight:bold;font-size:50px;color:grey;">编者按</span>', '&emsp;&emsp;Survival Guide（生存指南,简称SG)，是为了帮助新生了解在WPI的学习和生活情况，提前适应大学生活。在我来到WPI之前，他也给过我许多意想不到的帮助。<br>&emsp;&emsp;第十版SG是在第九版的基础上进行了校对和修正，删去了已经过时的内容，还添加了许多细节：比如很久未更新的专业，活动，衣食住行的经验等等。本次更新还在每页添加了返回目录功能，让读者更方便快捷地翻阅。<br>&emsp;&emsp;感谢前辈们对前九版SG创建和发展所作的不懈努力，感谢陆安琪主席对本次修订的大力支持，也感谢每个帮助编辑、投稿同学们的积极参与！<br>&emsp;&emsp;如果你对新版SG有任何建议或疑问，欢迎email至ywang15@wpi.edu。<br>&emsp;&emsp;祝愿每一位新同学能够通过SG，顺利地开始崭新的大学生活。也祝愿在WPI的每个同学学业有成，事事顺利！<p align="right">王耀奉 2019 CS</p>', 3);
INSERT INTO ARTICLES (TITLE, CONTENT ,MENU_ID) VALUES('<span style="font-weight:bold;font-size:50px;color:grey;">常用链接</span>', '<tab><b>学校主页</b><br>http://www.wpi.edu<tbr><b>WPI校园地图</b><br>http://www.wpi.edu/about/visitors/campusmap.html<tbr><b>学年日历</b><br>http://www.wpi.edu/calendars/index.html<tbr><b>MyWPI</b><br>http://my.wpi.edu<br>WPI的课程系统。教授会在里面发相关课程的大纲、作业、试验、阅读材料等等。<tbr><b>Banner System</b><br>http://bannerweb.wpi.edu<br>可以选课、查看成绩、注册等等。<tbr><b>邮件系统</b><br>http://exchange.wpi.edu<br>上面可以查到一学年中各门课的时间，地点和老师。<tbr><b>Catalog</b><br>http://www.wpi.edu/Pubs/Catalogs/Ugrad/index.html<tbr><b>Helpdesk</b><br>http://www.wpi.edu/Academics/CCC/Help/<br>可以帮助解决大多数的电脑问题<tbr><b>查询教授评价</b><br>www.ratemyprofessors.com<br>Or<br>http://bannerweb.wpi.edu<br>在banner登录之后, Student Services & Financial Aid > Additional Information for Students > WPI Student Course Reports</tab>', 4);
